/*! chess.min.js - minimal build of chess.js for PGN parsing */
window.Chess = function() {
  const moves = [];
  let fen = "start";

  return {
    load_pgn: function(pgn) {
      const tokens = pgn.replace(/\d+\./g, "").trim().split(/\s+/);
      for (let token of tokens) {
        if (token === "1-0" || token === "0-1" || token === "1/2-1/2" || token === "*") continue;
        moves.push(token);
      }
      // това е placeholder — не променя реална позиция
      fen = "start"; 
      return moves.length > 0;
    },
    fen: function() {
      return fen;
    },
    reset: function() {
      moves.length = 0;
      fen = "start";
    },
    move: function(move) {
      // placeholder, не извършва реално движение
      return true;
    }
  };
};
