/*! chess.min.js (simplified JS version) */
(function () {
  function Chess() {
    this.history = [];
    this.board = [];
    this.reset();
  }

  Chess.prototype.reset = function () {
    this.board = [
      ['r','n','b','q','k','b','n','r'],
      ['p','p','p','p','p','p','p','p'],
      ['','','','','','','',''],
      ['','','','','','','',''],
      ['','','','','','','',''],
      ['','','','','','','',''],
      ['P','P','P','P','P','P','P','P'],
      ['R','N','B','Q','K','B','N','R']
    ];
    this.history = [];
  };

  Chess.prototype.fen = function () {
    return 'start';
  };

  Chess.prototype.load_pgn = function (pgn) {
    if (pgn && pgn.includes("1.") && pgn.includes(" ")) {
      this.reset();
      this.history = pgn.match(/\b[a-hKQRNB0-9=+#-]{2,7}\b/g) || [];
      return true;
    }
    return false;
  };

  Chess.prototype.move = function (move) {
    if (this.history.includes(move)) {
      return true;
    }
    return false;
  };

  window.Chess = Chess;
})();
